class Lead:
    def __init__ (self, thickness: float, hardness: str, size: int):
        self.__thickness: float = thickness
        self.__hardness: str = hardness
        self.__size: int = size

    def __str__ (self):
        return f"[{self.__thickness}:{self.__hardness}:{self.__size}]"

    def getThickness (self):
        return self.__thickness

    def getSize (self):
        return self.__size

    def setSize(self, tamanho: int):
        self.__size = tamanho
        return self.__size

    def usagePerSheet (self):
        if self.__hardness == "HB":
            self.__size -= 1
            return 1
        if self.__hardness == "2B":
            self.__size -= 2
            return 2
        if self.__hardness == "4B":
            self.__size -= 4
            return 4
        if self.__hardness == "6B":
            self.__size -= 6
            return 6


class Pencil:
    def __init__(self, calibre: float, bico: Lead | None):
        self.calibre =  None
        self.bico = None
        self.barrel: list[Lead] = []

    def initLapiseira(self, tamanho: float):
        self.calibre = tamanho
        return True

    def __str__ (self):
        bico ="[]" if self.bico is None else f"{self.bico}"
        if self.barrel == []:
            tambor = "<>"
        else:
            pontas = "".join(str(x) for x in self.barrel)
            tambor = f"<{pontas}>"
        return f"calibre: {self.calibre}, bico: {bico}, tambor: {tambor}"

    def insert (self, grafite: Lead):
        if self.calibre != grafite.getThickness():
            print("fail: calibre incompatível")
            return
        self.barrel.append(grafite)

    def pull (self):
        if self.bico != None:
            print ("fail: ja existe grafite no bico")
            return
        self.bico = self.barrel[0]
        del self.barrel[0]

    def remove (self):
        if self.bico != None:
            self.bico = None
            return

    def write (self):
        if self.bico == None:
            print ("fail: nao existe grafite no bico")
            return
        if self.bico.getSize() == 10:
            print ("fail: tamanho insuficiente")
            return
        self.bico.usagePerSheet()
        if self.bico.getSize() < 10:
            self.bico.setSize(10)
            print("fail: folha incompleta")
            return        


def main():
    lapiseira = Pencil(0.0, None)

    while True:
        line = input()
        args: list[str] = line.split(" ")
        print("$" + line)

        if args [0] == "end":
            break
        elif args [0] == "init":
            tamanho = float(args[1])
            lapiseira.initLapiseira(tamanho)
        elif args [0] == "show":
            print(lapiseira)
        elif args [0] == "insert":
            thickness = float(args[1])
            hardness = args[2]
            size = int(args[3])
            grafite = Lead(thickness, hardness, size)
            lapiseira.insert(grafite)
        elif args [0] == "pull":
            lapiseira.pull()
        elif args [0] == "remove":
            lapiseira.remove()
        elif args [0] == "write":
            lapiseira.write()
        else:
            print("fail: comando invalido")

main()
